import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import "./styles/globals.css";
import 'tailwindcss/tailwind.css';

function App() {
  const [palavra, setPalavra] = useState(null);
  const [resposta, setResposta] = useState('');
  const [feedback, setFeedback] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [definicaoCompleta, setDefinicaoCompleta] = useState(null);

  const carregarPalavra = useCallback(async () => {
    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      setResposta('');
      
      const response = await axios.get('http://localhost:8000/api/palavra-aleatoria');
      setPalavra(response.data);
    } catch (error) {
      setFeedback("❌ Erro ao carregar palavra. Tente recarregar a página.");
    } finally {
      setCarregando(false);
    }
  }, []);

  const verificarResposta = async () => {
    if (!resposta.trim()) {
      setFeedback("❌ Por favor, digite uma resposta");
      return;
    }

    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      
      const response = await axios.post('http://localhost:8000/api/verificar', {
        palavra: palavra.termo,
        resposta: resposta.trim()
      });
      
      setFeedback(response.data.feedback);
      setDefinicaoCompleta(response.data.definicao_correta || null);
    } catch (error) {
      setFeedback("❌ Erro ao verificar resposta. Tente novamente.");
    } finally {
      setCarregando(false);
    }
  };

  useEffect(() => { 
    carregarPalavra();
  }, [carregando]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-start pt-8 bg-[#121213]">
      <div className="w-full max-w-md px-4">
        {/* Cabeçalho */}
        <header className="border-b border-[#3a3a3c] pb-4 mb-6">
          <h1 className="text-3xl font-bold text-center text-white uppercase tracking-wider">Adivinhe o Significado</h1>
        </header>

        {/* Área da palavra */}
        {palavra && (
          <div className="mb-8">
            <div className="flex justify-center mb-6">
              <div className="text-4xl font-bold text-white uppercase tracking-widest">
                {palavra.termo}
              </div>
            </div>
            
            {/* Dica e exemplo */}
            <div className="text-center">
              <p className="text-sm text-[#d7dadc] mb-1">Dica: {palavra.categoria}</p>
              {palavra.frases?.[0] && (
                <p className="text-sm italic text-[#d7dadc] mt-2 pl-2 border-l-2 border-[#565758]">
                  Exemplo: "{palavra.frases[0]}"
                </p>
              )}
            </div>
          </div>
        )}

        {/* Campo de entrada */}
        <div className="mb-6">
          <label htmlFor="meaning-input" className="block text-sm text-[#d7dadc] mb-2">
            Digite o significado
          </label>
          <input
            id="meaning-input"
            type="text"
            value={resposta}
            onChange={(e) => setResposta(e.target.value)}
            className="w-full p-3 rounded text-center text-lg font-medium bg-[#3a3a3c] border border-[#565758] text-white focus:outline-none focus:ring-2 focus:ring-[#565758]"
            placeholder=""
            onKeyDown={(e) => e.key === 'Enter' && !carregando && verificarResposta()}
            disabled={carregando}
          />
        </div>

        {/* Botões */}
        <div className="flex gap-3 mb-6">
          <button
            onClick={verificarResposta}
            className={`flex-1 py-3 rounded font-bold transition-colors duration-200 ${
              carregando || !resposta.trim() 
                ? 'bg-gray-600 text-gray-400 cursor-not-allowed' 
                : 'bg-[#538d4e] text-white hover:bg-[#4a7c45]'
            }`}
            disabled={carregando || !resposta.trim()}
          >
            Verificar
          </button>
          <button
            onClick={carregarPalavra}
            className="flex-1 py-3 rounded font-bold bg-[#3a3a3c] text-white hover:bg-[#565758] transition-colors duration-200"
          >
            Próxima palavra
          </button>
        </div>

        {/* Feedback */}
        {feedback && (
          <div className="min-h-20">
            <div className={`p-3 rounded-lg text-center font-medium ${
              feedback.includes('✅') ? 'bg-[#538d4e] text-white' :
              feedback.includes('⚠️') ? 'bg-[#b59f3b] text-white' :
              'bg-[#3a3a3c] text-white'
            }`}>
              <p>{feedback.replace(/[✅⚠️❌]/g, '').trim()}</p>
              {definicaoCompleta && (
                <p className="mt-2 text-sm">Definição correta: {definicaoCompleta}</p>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
