import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import "./styles/globals.css";
import 'tailwindcss/tailwind.css';

function App() {
  const [palavra, setPalavra] = useState(null);
  const [resposta, setResposta] = useState('');
  const [feedback, setFeedback] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [definicaoCompleta, setDefinicaoCompleta] = useState(null);

  const carregarPalavra = useCallback(async () => {
    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      setResposta('');
      
      const response = await axios.get('http://localhost:8000/api/palavra-aleatoria');
      setPalavra(response.data);
    } catch (error) {
      setFeedback("❌ Erro ao carregar palavra. Tente recarregar a página.");
    } finally {
      setCarregando(false);
    }
  }, []);

  const verificarResposta = async () => {
    if (!resposta.trim()) {
      setFeedback("❌ Por favor, digite uma resposta");
      return;
    }

    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      
      const response = await axios.post('http://localhost:8000/api/verificar', {
        palavra: palavra.termo,
        resposta: resposta.trim()
      });
      
      setFeedback(response.data.feedback);
      setDefinicaoCompleta(response.data.definicao_correta || null);
    } catch (error) {
      setFeedback("❌ Erro ao verificar resposta. Tente novamente.");
    } finally {
      setCarregando(false);
    }
  };

  useEffect(() => { 
    carregarPalavra();
  }, [carregarPalavra]);

  return (
    <div className="min-h-screen bg-[#4D3B3B] flex flex-col items-center justify-center p-4">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h1 className="text-2xl font-bold mb-4 text-center">Adivinhe o Significado</h1>
        
        {palavra && (
          <div className="mb-6">
            <p className="text-lg font-semibold text-center">{palavra.termo}</p>
            <p className="text-sm text-gray-600 text-center">Dica: {palavra.categoria}</p>
            
            {palavra.frases?.[0] && (
              <div className="mt-2 text-sm italic bg-gray-50 p-2 rounded text-center">
                <p>Exemplo: "{palavra.frases[0]}"</p>
              </div>
            )}
          </div>
        )}
        
        <input
          type="text"
          value={resposta}
          onChange={(e) => setResposta(e.target.value)}
          className="w-full p-3 border rounded mb-4 focus:ring-2 focus:ring-yellow-500 focus:border-transparent"
          placeholder="Digite o significado..."
          onKeyDown={(e) => e.key === 'Enter' && !carregando && verificarResposta()}
          disabled={carregando}
        />
        
        <button
          onClick={verificarResposta}
          className={`w-full py-3 rounded-full transition-colors text-white font-bold ${
            carregando || !resposta.trim() 
              ? 'bg-gray-400 cursor-not-allowed' 
              : 'bg-yellow-500 hover:bg-yellow-600'
          }`}
          disabled={carregando || !resposta.trim()}
        >
          {carregando ? 'Verificando...' : 'Verificar'}
        </button>
        
        {feedback && (
          <div className={`mt-4 p-3 text-center rounded-lg ${
            feedback.includes('✅') ? 'bg-green-100 text-green-800' :
            feedback.includes('⚠️') ? 'bg-yellow-100 text-yellow-800' :
            'bg-red-100 text-red-800'
          }`}>
            <p className="font-medium">{feedback}</p>
            {definicaoCompleta && (
              <p className="mt-2 text-sm">Definição correta: {definicaoCompleta}</p>
            )}
          </div>
        )}
        
        {palavra && !carregando && (
          <button
            onClick={carregarPalavra}
            className="mt-4 w-full py-3 rounded-full bg-blue-500 hover:bg-blue-600 text-white font-bold"
          >
            Próxima palavra
          </button>
        )}
      </div>
    </div>
  );
}

export default App;
