import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [palavra, setPalavra] = useState(null);
  const [resposta, setResposta] = useState('');
  const [feedback, setFeedback] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [definicaoCompleta, setDefinicaoCompleta] = useState(null);

  const carregarPalavra = async () => {
    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      setResposta('');
      
      const response = await axios.get('http://localhost:8000/api/palavra-aleatoria');
      setPalavra(response.data);
    } catch (error) {
      console.error("Erro detalhado:", error.response?.data || error.message);
      setFeedback("❌ Erro ao carregar palavra. Tente recarregar a página.");
    } finally {
      setCarregando(false);
    }
  };
  
  const verificarResposta = async () => {
    if (!resposta.trim()) {
      setFeedback("❌ Por favor, digite uma resposta");
      return;
    }

    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      
      const response = await axios.post('http://localhost:8000/api/verificar', {
        palavra: palavra.termo,
        resposta: resposta.trim()
      });

      console.log("Resposta da API:", response.data); // Para depuração
      
      setFeedback(response.data.feedback);
      setDefinicaoCompleta(response.data.definicao_correta || null);
      
    } catch (error) {
      console.error("Erro detalhado:", {
        status: error.response?.status,
        data: error.response?.data,
        message: error.message
      });
      setFeedback("❌ Erro ao verificar resposta. Tente novamente.");
    } finally {
      setCarregando(false);
    }
  };

  useEffect(() => { 
    carregarPalavra();
  }, []);

  useEffect(() => {
    console.log("Feedback atualizado:", feedback);
    console.log("Definição completa atualizada:", definicaoCompleta);
  }, [feedback, definicaoCompleta]);

  if (!palavra && !feedback) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-gray-800 flex flex-col items-center justify-start pt-12 px-4">
      {/* Cabeçalho com nome do jogo */}
      <div className="mb-8 text-center">
        <h1 className="text-4xl font-bold text-white mb-2">ADIVINHE O SIGNIFICADO</h1>
        <div className="h-1 w-20 bg-blue-500 mx-auto rounded-full"></div>
      </div>

      {/* Container principal */}
      <div className="w-full max-w-md bg-gray-800 rounded-xl shadow-2xl border border-gray-700 p-6">
        {/* Área da palavra e dica */}
        {palavra && (
          <div className="mb-6 text-center">
            <div className="flex justify-center mb-4">
              {[...Array(3)].map((_, i) => (
                <div key={i} className="w-12 h-12 mx-1 bg-gray-700 rounded-md flex items-center justify-center">
                  <span className="text-2xl font-bold text-white">
                    {palavra.termo[i] || ''}
                  </span>
                </div>
              ))}
            </div>
            <p className="text-sm text-gray-400">Dica: {palavra.categoria}</p>
          </div>
        )}

        {/* Campo de digitação */}
        <div className="mb-6">
          <input
            type="text"
            value={resposta}
            onChange={(e) => setResposta(e.target.value)}
            className="w-full px-4 py-3 bg-gray-700 text-white rounded-lg border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Digite o significado..."
            onKeyPress={(e) => e.key === 'Enter' && !carregando && verificarResposta()}
            disabled={carregando}
          />
        </div>

        {/* Botões de ação */}
        <div className="grid grid-cols-2 gap-4">
          <button
            onClick={verificarResposta}
            className={`py-3 px-4 rounded-lg font-medium transition-colors ${
              carregando || !resposta.trim()
                ? 'bg-gray-600 text-gray-400 cursor-not-allowed'
                : 'bg-blue-600 hover:bg-blue-700 text-white'
            }`}
            disabled={carregando || !resposta.trim()}
          >
            VERIFICAR
          </button>

          <button
            onClick={carregarPalavra}
            className="py-3 px-4 bg-gray-700 hover:bg-gray-600 text-white rounded-lg font-medium transition-colors"
          >
            PRÓXIMA PALAVRA
          </button>
        </div>

        {/* Feedback */}
        {feedback && (
          <div className={`mt-6 p-4 rounded-lg ${
            feedback.includes('✅') ? 'bg-green-900 text-green-200' :
            feedback.includes('⚠️') ? 'bg-yellow-900 text-yellow-200' :
            'bg-red-900 text-red-200'
          }`}>
            <p className="font-medium">{feedback}</p>
            {definicaoCompleta && (
              <p className="mt-2 text-sm">Definição correta: {definicaoCompleta}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
