import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [palavra, setPalavra] = useState(null);
  const [resposta, setResposta] = useState('');
  const [feedback, setFeedback] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [definicaoCompleta, setDefinicaoCompleta] = useState(null);

  const carregarPalavra = async () => {
    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      setResposta('');
      
      const response = await axios.get('http://localhost:8000/api/palavra-aleatoria');
      setPalavra(response.data);
    } catch (error) {
      setFeedback("❌ Erro ao carregar palavra. Tente recarregar a página.");
    } finally {
      setCarregando(false);
    }
  };
  
  const verificarResposta = async () => {
    if (!resposta.trim()) {
      setFeedback("❌ Por favor, digite uma resposta");
      return;
    }

    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      
      const response = await axios.post('http://localhost:8000/api/verificar', {
        palavra: palavra.termo,
        resposta: resposta.trim()
      });
      
      setFeedback(response.data.feedback);
      setDefinicaoCompleta(response.data.definicao_correta || null);
    } catch (error) {
      setFeedback("❌ Erro ao verificar resposta. Tente novamente.");
    } finally {
      setCarregando(false);
    }
  };

  useEffect(() => { 
    carregarPalavra();
  }, []);

  return (
    <div className="min-h-screen bg-[#5C3B3B] flex flex-col items-center justify-center p-4 text-white">
      <div className="bg-[#3D2C2C] p-6 rounded-lg shadow-lg w-full max-w-md text-center">
        <h1 className="text-3xl font-bold mb-4">Adivinhe o Significado</h1>
        {palavra && (
          <div className="mb-6">
            <p className="text-2xl font-semibold">{palavra.termo}</p>
            <p className="text-sm text-gray-300">Dica: {palavra.categoria}</p>
          </div>
        )}

        <input
          type="text"
          value={resposta}
          onChange={(e) => setResposta(e.target.value)}
          className="w-full p-3 border-none rounded-md bg-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-yellow-500"
          placeholder="Digite o significado..."
          onKeyPress={(e) => e.key === 'Enter' && !carregando && verificarResposta()}
          disabled={carregando}
        />

        <button
          onClick={verificarResposta}
          className={`mt-4 w-full py-3 rounded-full text-white font-bold transition-colors ${
            carregando || !resposta.trim()
              ? 'bg-gray-500 cursor-not-allowed' 
              : 'bg-yellow-500 hover:bg-yellow-600'
          }`}
          disabled={carregando || !resposta.trim()}
        >
          {carregando ? 'Verificando...' : 'Verificar'}
        </button>

        {feedback && (
          <div className={`mt-4 p-3 rounded-lg ${
            feedback.includes('✅') ? 'bg-green-500' :
            feedback.includes('⚠️') ? 'bg-yellow-500' :
            'bg-red-500'
          }`}> 
            <p className="font-medium">{feedback}</p>
            {definicaoCompleta && <p className="mt-2 text-sm">Definição correta: {definicaoCompleta}</p>}
          </div>
        )}

        <button
          onClick={carregarPalavra}
          className="mt-4 w-full py-3 rounded-full bg-blue-500 hover:bg-blue-600 text-white font-bold"
        >
          Próxima palavra
        </button>
      </div>
    </div>
  );
}

export default App;
