import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [palavra, setPalavra] = useState(null);
  const [resposta, setResposta] = useState('');
  const [feedback, setFeedback] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [definicaoCompleta, setDefinicaoCompleta] = useState(null);

  const carregarPalavra = async () => {
    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      setResposta('');
      
      const response = await axios.get('http://localhost:8000/api/palavra-aleatoria');
      setPalavra(response.data);
    } catch (error) {
      console.error("Erro detalhado:", error.response?.data || error.message);
      setFeedback("❌ Erro ao carregar palavra. Tente recarregar a página.");
    } finally {
      setCarregando(false);
    }
  };
  
  const verificarResposta = async () => {
    if (!resposta.trim()) {
      setFeedback("❌ Por favor, digite uma resposta");
      return;
    }

    try {
      setCarregando(true);
      setFeedback(null);
      setDefinicaoCompleta(null);
      
      const response = await axios.post('http://localhost:8000/api/verificar', {
        palavra: palavra.termo,
        resposta: resposta.trim()
      });

      console.log("Resposta da API:", response.data);
      
      setFeedback(response.data.feedback);
      setDefinicaoCompleta(response.data.definicao_correta || null);
      
    } catch (error) {
      console.error("Erro detalhado:", {
        status: error.response?.status,
        data: error.response?.data,
        message: error.message
      });
      setFeedback("❌ Erro ao verificar resposta. Tente novamente.");
    } finally {
      setCarregando(false);
    }
  };

  useEffect(() => { 
    carregarPalavra();
  }, []);

  if (!palavra && !feedback) {
    return (
      <div className="min-h-screen bg-amber-900 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-amber-200"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-amber-900 flex flex-col items-center justify-start pt-12 px-4">
      {/* Cabeçalho */}
      <div className="mb-8 text-center">
        <h1 className="text-4xl font-bold text-amber-100 mb-2">ADIVINHE O SIGNIFICADO</h1>
        <div className="h-1 w-20 bg-amber-600 mx-auto rounded-full"></div>
      </div>

      {/* Container principal */}
      <div className="w-full max-w-md bg-amber-800 rounded-lg shadow-lg border-2 border-amber-700 p-6">
        {/* Área da palavra */}
        {palavra && (
          <div className="mb-6 text-center">
            <div className="flex justify-center mb-4">
              {[...Array(3)].map((_, i) => (
                <div 
                  key={i} 
                  className="w-14 h-14 mx-1 bg-amber-700 rounded-md flex items-center justify-center border-2 border-amber-600"
                >
                  <span className="text-3xl font-bold text-amber-100 uppercase">
                    {palavra.termo[i] || ''}
                  </span>
                </div>
              ))}
            </div>
            <p className="text-sm text-amber-300">Dica: {palavra.categoria}</p>
            
            {palavra.frases?.[0] && (
              <div className="mt-4 text-sm italic text-amber-200 bg-amber-900 bg-opacity-50 p-3 rounded border-l-4 border-amber-600">
                <p>"{palavra.frases[0]}"</p>
              </div>
            )}
          </div>
        )}
        
        {/* Campo de texto */}
        <div className="mb-6">
          <input
            type="text"
            value={resposta}
            onChange={(e) => setResposta(e.target.value)}
            className="w-full px-4 py-3 bg-amber-100 text-amber-900 rounded-lg border-2 border-amber-600 focus:outline-none focus:ring-2 focus:ring-amber-500 focus:border-amber-500 placeholder-amber-600"
            placeholder="Digite o significado..."
            onKeyPress={(e) => e.key === 'Enter' && !carregando && verificarResposta()}
            disabled={carregando}
          />
        </div>
        
        {/* Botões */}
        <div className="grid grid-cols-2 gap-4">
          <button
            onClick={verificarResposta}
            className={`py-3 px-4 rounded-lg font-bold transition-colors ${
              carregando || !resposta.trim()
                ? 'bg-amber-500 text-amber-200 cursor-not-allowed'
                : 'bg-amber-600 hover:bg-amber-700 text-amber-100'
            }`}
            disabled={carregando || !resposta.trim()}
          >
            {carregando ? 'VERIFICANDO...' : 'VERIFICAR'}
          </button>

          <button
            onClick={carregarPalavra}
            className="py-3 px-4 bg-amber-700 hover:bg-amber-800 text-amber-100 rounded-lg font-bold transition-colors"
          >
            PRÓXIMA PALAVRA
          </button>
        </div>
        
        {/* Feedback */}
        {feedback && (
          <div className={`mt-6 p-4 rounded-lg border-2 ${
            feedback.includes('✅') ? 'bg-green-900 border-green-700 text-green-100' :
            feedback.includes('⚠️') ? 'bg-yellow-900 border-yellow-700 text-yellow-100' :
            'bg-red-900 border-red-700 text-red-100'
          }`}>
            <p className="font-bold">{feedback}</p>
            {definicaoCompleta && (
              <p className="mt-2 text-sm font-medium">Definição correta: {definicaoCompleta}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
