import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [palavra, setPalavra] = useState(null);
  const [resposta, setResposta] = useState('');
  const [feedback, setFeedback] = useState(null);

  const carregarPalavra = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/palavra-aleatoria'); // Porta corrigida para 8000
      setPalavra(response.data);
      setResposta('');
      setFeedback(null);
    } catch (error) {
      console.error("Erro ao carregar palavra:", error);
      alert("Erro ao carregar palavra. Tente novamente.");
    }
  };

  const verificarResposta = async () => {
    try {
      const response = await axios.post('http://localhost:8000/api/verificar', { // Porta corrigida para 8000
        palavra: palavra.termo,
        resposta
      });
      setFeedback(response.data.acerto ? '✅ Acertou!' : '❌ Tente novamente');
      setTimeout(() => carregarPalavra(), 1500); // Carrega nova palavra após 1.5s
    } catch (error) {
      console.error("Erro ao verificar resposta:", error);
      alert("Erro ao verificar resposta. Tente novamente.");
    }
  };

  useEffect(() => { 
    carregarPalavra(); 
  }, []);

  if (!palavra) return <div className="min-h-screen flex items-center justify-center">Carregando...</div>;

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
      <div className="bg-white p-8 rounded-lg shadow-md w-96">
        <h1 className="text-2xl font-bold mb-4">Adivinhe o Significado</h1>
        
        <div className="mb-6">
          <p className="text-lg mb-2">Palavra: <span className="font-semibold">{palavra.termo}</span></p>
          <p className="text-sm text-gray-600">Dica: {palavra.categoria}</p>
          {palavra.frases && palavra.frases.length > 0 && (
            <div className="mt-2 text-sm italic">
              <p>Exemplo: "{palavra.frases[0]}"</p>
            </div>
          )}
        </div>
        
        <input
          type="text"
          value={resposta}
          onChange={(e) => setResposta(e.target.value)}
          className="w-full p-2 border rounded mb-4"
          placeholder="Seu palpite..."
          onKeyPress={(e) => e.key === 'Enter' && verificarResposta()}
        />
        
        <button
          onClick={verificarResposta}
          className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition-colors"
          disabled={!resposta.trim()}
        >
          Verificar
        </button>

        {feedback && (
          <div className={`mt-4 p-2 text-center rounded ${feedback.includes('✅') ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
            {feedback}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
